  container: {
    flex: 1,

    backgroundColor: "#F6F6F6",
  },
  header: {
    paddingTop: "8%",
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 25,
  },
  input1: {
    height: 40,
    paddingLeft: "5%",
    borderWidth: 1,
    borderColor: "white",
    textAlignVertical: "top",
  },
  shadow1: {
    marginLeft: "5%",
    shadowColor: "black",
    shadowOpacity: 0.4,
    shadowRadius: 3,
    shadowOffset: {
      height: 0,
      width: 0,
    },
    elevation: 2,
  },
  input: {
    paddingLeft: "2%",
    borderWidth: 1,
    borderColor: "black",
    textAlignVertical: "top",
  },
  input2: {
    marginLeft: "3%",
    height: 40,
    paddingLeft: "2%",
    borderBottomWidth: 1,
    borderColor: "black",
    textAlignVertical: "top",
  },
  button: {
    justifyContent: "center",
    marginTop: "10%",
    width: "50%",
    height: 70,
    backgroundColor: "#0097FF",
    borderRadius: Platform.OS === "ios" ? "15%" : 15,
  },
  flatbutton: {
    height: 30,
    width: "30%",
    justifyContent: "center",
    marginRight: 5,
  },

  image: {
    width: "100%",
    height: 300,
    resizeMode: "contain",
  },
});
